"use client";

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import Image from 'next/image';
import Navbar from '@/components/Navbar';
import Footer from '@/components/Footer';
import { ArrowLeft, ArrowRight, Calendar, Clock, Share2, Sparkles, List, ChevronRight } from 'lucide-react';
import { motion, useScroll, useSpring, AnimatePresence } from 'framer-motion';
import { BlogPost, blogPosts } from '@/data/blog-posts';

interface BlogPostContentProps {
    post: BlogPost;
}

export default function BlogPostContent({ post }: BlogPostContentProps) {
    const [isTocOpen, setIsTocOpen] = useState(false);
    const { scrollYProgress } = useScroll();
    const scaleX = useSpring(scrollYProgress, {
        stiffness: 100,
        damping: 30,
        restDelta: 0.001
    });

    // Close TOC on click outside or escape
    useEffect(() => {
        const handleEscape = (e: KeyboardEvent) => {
            if (e.key === 'Escape') setIsTocOpen(false);
        };
        window.addEventListener('keydown', handleEscape);
        return () => window.removeEventListener('keydown', handleEscape);
    }, []);

    return (
        <div className="min-h-screen bg-[#FDFCF8] text-[#004D40] selection:bg-[#FF6B35]/30 scroll-smooth">
            {/* Scroll Progress Bar */}
            <motion.div
                className="fixed top-0 left-0 right-0 h-1.5 bg-[#FF6B35] origin-left z-[100]"
                style={{ scaleX }}
            />

            <Navbar />

            {/* Mobile TOC Trigger */}
            <div className="fixed bottom-8 right-8 z-[90] lg:hidden">
                <button
                    onClick={() => setIsTocOpen(!isTocOpen)}
                    className="w-14 h-14 bg-[#004D40] text-white rounded-full shadow-2xl flex items-center justify-center hover:scale-110 active:scale-95 transition-transform"
                >
                    <List className="w-6 h-6" />
                </button>
            </div>

            {/* TOC Overlay for Mobile */}
            <AnimatePresence>
                {isTocOpen && (
                    <motion.div
                        initial={{ opacity: 0, x: 100 }}
                        animate={{ opacity: 1, x: 0 }}
                        exit={{ opacity: 0, x: 100 }}
                        className="fixed inset-y-0 right-0 w-full sm:w-80 bg-white shadow-2xl z-[100] p-8 border-l border-[#004D40]/5 lg:hidden"
                    >
                        <div className="flex items-center justify-between mb-12">
                            <h3 className="text-xl font-black tracking-tighter uppercase">Table of Contents</h3>
                            <button onClick={() => setIsTocOpen(false)} className="text-[#004D40]/40 font-black uppercase text-xs">Close</button>
                        </div>
                        <nav className="space-y-6">
                            {post.sections.map((section, idx) => (
                                <a
                                    key={idx}
                                    href={`#section-${idx}`}
                                    onClick={() => setIsTocOpen(false)}
                                    className="flex items-start gap-4 group"
                                >
                                    <span className="text-[#FF6B35] font-black text-xs pt-1">0{idx + 1}</span>
                                    <span className="text-lg font-bold group-hover:text-[#FF6B35] transition-colors line-clamp-2">{section.heading}</span>
                                </a>
                            ))}
                        </nav>
                    </motion.div>
                )}
            </AnimatePresence>

            <main className="pt-24 sm:pt-32 md:pt-48 pb-20 sm:pb-32">
                <div className="max-w-[1440px] mx-auto px-4 sm:px-6 lg:px-12 grid grid-cols-1 lg:grid-cols-[1fr_300px] gap-12 xl:gap-24 items-start">

                    <article className="max-w-4xl mx-auto w-full">
                        {/* Header Controls */}
                        <div className="flex flex-col sm:flex-row items-center justify-between mb-8 sm:mb-16 gap-4 sm:gap-6 border-b border-[#004D40]/5 pb-6 sm:pb-8">
                            <Link href="/blog" className="inline-flex items-center font-black text-[10px] sm:text-[11px] uppercase tracking-[0.3em] opacity-40 hover:opacity-100 hover:text-[#FF6B35] transition-all group">
                                <ArrowLeft className="mr-2 sm:mr-3 w-3.5 h-3.5 sm:w-4 sm:h-4 group-hover:-translate-x-2 transition-transform" />
                                Return to Library
                            </Link>
                            <div className="flex items-center gap-4 sm:gap-10">
                                <div className="flex items-center gap-3 sm:gap-4 text-[10px] sm:text-[11px] font-black text-[#004D40]/40 uppercase tracking-[0.2em]">
                                    <span className="flex items-center gap-1.5 sm:gap-2"><Calendar className="w-3.5 h-3.5 sm:w-4 sm:h-4 text-[#FF6B35]" /> {post.date}</span>
                                    <span className="w-1 h-1 rounded-full bg-[#004D40]/10" />
                                    <span className="flex items-center gap-1.5 sm:gap-2"><Clock className="w-3.5 h-3.5 sm:w-4 sm:h-4" /> {post.readTime}</span>
                                </div>
                                <button className="p-2 sm:p-3 bg-white border border-[#004D40]/5 rounded-xl sm:rounded-2xl hover:bg-[#004D40] hover:text-white transition-all shadow-sm active:scale-95 group">
                                    <Share2 className="w-3.5 h-3.5 sm:w-4 sm:h-4 group-hover:scale-110 transition-transform" />
                                </button>
                            </div>
                        </div>

                        {/* Title Header */}
                        <header className="space-y-6 sm:space-y-12 mb-12 sm:mb-32 text-center sm:text-left">
                            <motion.div
                                initial={{ opacity: 0, x: -20 }}
                                animate={{ opacity: 1, x: 0 }}
                                className="inline-block px-4 sm:px-6 py-2 sm:py-2.5 bg-[#004D40] text-white text-[10px] sm:text-[11px] font-black tracking-[0.3em] rounded-full uppercase shadow-xl shadow-[#004D40]/10"
                            >
                                {post.category}
                            </motion.div>

                            <motion.h1
                                initial={{ opacity: 0, y: 30 }}
                                animate={{ opacity: 1, y: 0 }}
                                className="text-4xl min-[420px]:text-5xl sm:text-7xl md:text-8xl lg:text-[100px] font-black leading-[1] sm:leading-[0.9] tracking-tighter text-balance"
                            >
                                {post.title}
                            </motion.h1>

                            <div className="flex flex-col sm:flex-row items-center gap-4 sm:gap-8 pt-8 sm:pt-12 border-t border-[#004D40]/5">
                                <div className="relative">
                                    <div className="w-12 h-12 sm:w-20 sm:h-20 rounded-xl sm:rounded-[2rem] bg-[#FF6B35] flex items-center justify-center text-white font-black shadow-2xl text-lg sm:text-3xl transform hover:rotate-6 transition-transform">AL</div>
                                    <div className="absolute -bottom-1 -right-1 w-5 h-5 sm:w-8 sm:h-8 bg-white rounded-full flex items-center justify-center shadow-lg border border-[#004D40]/5">
                                        <Sparkles className="w-2.5 h-2.5 sm:w-4 sm:h-4 text-[#FF6B35]" />
                                    </div>
                                </div>
                                <div className="text-center sm:text-left">
                                    <p className="text-[10px] sm:text-[11px] font-black uppercase tracking-[0.3em] sm:tracking-[0.4em] opacity-30 leading-none mb-1.5 sm:mb-3">Published By</p>
                                    <p className="text-base sm:text-xl font-black text-[#004D40]">Ahmd Luthu <span className="block sm:inline-block mt-2 sm:mt-0 sm:ml-3 px-3 sm:px-4 py-1 sm:py-1.5 bg-[#004D40]/5 rounded-full text-[9px] sm:text-[10px] uppercase font-black opacity-60 border border-[#004D40]/10">Verified Strategist</span></p>
                                </div>
                            </div>
                        </header>

                        {/* Hero Image */}
                        <motion.div
                            initial={{ opacity: 0, scale: 0.98 }}
                            animate={{ opacity: 1, scale: 1 }}
                            transition={{ duration: 1.2, ease: [0.22, 1, 0.36, 1] }}
                            className="relative h-[280px] min-[420px]:h-[350px] sm:h-[550px] md:h-[750px] rounded-3xl sm:rounded-[4rem] lg:rounded-[6rem] overflow-hidden mb-12 sm:mb-40 shadow-[0_40px_100px_rgba(0,0,0,0.1)] group"
                        >
                            <Image
                                src={post.image}
                                alt={post.title}
                                fill
                                className="object-cover transition-transform duration-[3000ms] group-hover:scale-105"
                                priority
                                sizes="(max-width: 1440px) 100vw, 1200px"
                            />
                            <div className="absolute inset-0 bg-gradient-to-t from-[#004D40]/30 to-transparent" />
                        </motion.div>

                        {/* Rich Content Section */}
                        <div className="max-w-3xl mx-auto">
                            <div className="prose prose-base sm:prose-2xl prose-emerald max-w-none prose-headings:font-black prose-headings:tracking-tighter prose-p:text-[#004D40]/80 sm:prose-p:leading-[1.9] prose-p:leading-relaxed">
                                <p className="text-lg sm:text-4xl text-[#004D40] font-black leading-[1.4] sm:leading-[1.25] mb-12 sm:mb-24 italic border-l-[6px] sm:border-l-[12px] border-[#FF6B35] pl-6 sm:pl-16 py-8 sm:py-16 bg-white rounded-r-2xl sm:rounded-r-[4rem] shadow-[20px_20px_60px_rgba(0,0,0,0.02)] border border-[#004D40]/5 relative overflow-hidden group">
                                    <span className="absolute -top-10 -right-10 w-24 h-24 sm:w-40 sm:h-40 bg-[#FF6B35]/5 rounded-full blur-[40px] sm:blur-[60px]" />
                                    {post.intro}
                                </p>

                                <div className="space-y-16 sm:space-y-48">
                                    {post.sections.map((section, idx) => (
                                        <motion.div
                                            key={idx}
                                            id={`section-${idx}`}
                                            initial={{ opacity: 0, y: 40 }}
                                            whileInView={{ opacity: 1, y: 0 }}
                                            viewport={{ once: true, margin: "-100px" }}
                                            className="gap-6 sm:gap-20 flex flex-col"
                                        >
                                            <div className="flex items-center gap-4 sm:gap-6">
                                                <div className="w-10 sm:w-20 h-[3px] bg-[#FF6B35] rounded-full" />
                                                <span className="text-[10px] sm:text-[11px] font-black uppercase tracking-[0.4em] text-[#FF6B35]">Knowledge Block {idx + 1}</span>
                                            </div>
                                            <div className="space-y-6 sm:space-y-12">
                                                <h2 className="text-3xl min-[420px]:text-4xl sm:text-6xl md:text-7xl font-black text-[#004D40] leading-[1.1] tracking-tighter mt-0">
                                                    {section.heading}
                                                </h2>
                                                <div className="text-[#004D40]/80 text-lg sm:text-2xl font-medium leading-[1.7] sm:leading-[1.9]">
                                                    {section.content}
                                                </div>
                                            </div>
                                        </motion.div>
                                    ))}
                                </div>

                                <motion.div
                                    initial={{ opacity: 0 }}
                                    whileInView={{ opacity: 1 }}
                                    className="mt-24 sm:mt-56 pt-12 sm:pt-32 border-t-4 border-[#004D40]/5 text-center"
                                >
                                    <div className="relative inline-block">
                                        <span className="absolute -top-12 sm:-top-20 left-1/2 -translate-x-1/2 text-7xl sm:text-[120px] font-black text-[#FF6B35]/5 pointer-events-none uppercase tracking-widest italic">Conclusion</span>
                                        <p className="text-2xl sm:text-6xl font-black text-[#004D40]/90 leading-tight italic tracking-tighter decoration-[#FF6B35]/20 underline decoration-[6px] sm:decoration-[10px] underline-offset-[8px] sm:underline-offset-[16px] relative z-10">
                                            &quot;{post.closing}&quot;
                                        </p>
                                    </div>
                                </motion.div>
                            </div>

                            {/* Author Box - Revamped for High-End Feel */}
                            <div className="mt-24 sm:mt-64 p-8 sm:p-24 bg-white rounded-[3rem] sm:rounded-[8rem] border border-[#004D40]/5 shadow-[0_60px_120px_rgba(0,0,0,0.06)] relative overflow-hidden group">
                                <div className="absolute top-0 right-0 w-full h-full bg-[radial-gradient(circle_at_top_right,_rgba(255,107,53,0.05)_0%,_transparent_60%)]" />

                                <div className="flex flex-col xl:flex-row items-center xl:items-start gap-12 sm:gap-24 relative z-10 text-center xl:text-left">
                                    <div className="shrink-0 space-y-6 sm:space-y-10">
                                        <div className="relative inline-block group">
                                            <div className="w-28 h-28 sm:w-64 sm:h-64 bg-[#004D40] rounded-[2rem] sm:rounded-[5.5rem] flex items-center justify-center text-white text-4xl sm:text-8xl font-black shadow-2xl transition-all duration-1000 group-hover:rotate-0 rotate-3 overflow-hidden">
                                                AL
                                                <div className="absolute inset-0 bg-gradient-to-br from-white/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />
                                            </div>
                                            <motion.div
                                                animate={{ rotate: 360 }}
                                                transition={{ duration: 10, repeat: Infinity, ease: "linear" }}
                                                className="absolute -bottom-4 -right-4 w-12 h-12 sm:w-24 sm:h-24 bg-[#FF6B35] rounded-full flex items-center justify-center text-white shadow-xl border-4 border-white"
                                            >
                                                <Sparkles className="w-5 h-5 sm:w-10 sm:h-10" />
                                            </motion.div>
                                        </div>
                                        <div className="space-y-2 sm:space-y-4">
                                            <p className="text-[10px] sm:text-[12px] font-black uppercase tracking-[0.5em] text-[#FF6B35]">Industry Leader</p>
                                            <div className="flex justify-center xl:justify-start gap-2 sm:gap-3">
                                                {[1, 2, 3, 4, 5].map(i => <div key={i} className="w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full bg-[#004D40]/10" />)}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="space-y-8 sm:space-y-14 flex-grow">
                                        <div className="space-y-3 sm:space-y-4">
                                            <h3 className="text-4xl sm:text-7xl font-black text-[#004D40] tracking-[calc(-0.04em)] leading-none">Ahmd Luthu</h3>
                                            <p className="text-[#FF6B35] font-black text-[12px] sm:text-lg uppercase tracking-[0.4em]">The Best Marketer in Kannur</p>
                                        </div>
                                        <p className="text-[#004D40]/70 text-lg sm:text-3xl font-medium leading-[1.6] max-w-2xl mx-auto xl:mx-0">
                                            Pioneering AI-first strategies that don't just generate noiseΓÇöthey build compounding growth engines for visionary brands.
                                        </p>
                                        <div className="pt-4 sm:pt-6 flex flex-col sm:flex-row gap-4 sm:gap-8 justify-center xl:justify-start">
                                            <Link
                                                href="/#contact"
                                                className="px-10 sm:px-14 py-5 sm:py-7 bg-[#004D40] text-white font-black rounded-2xl sm:rounded-[3rem] shadow-xl hover:shadow-[#004D40]/20 hover:bg-[#FF6B35] transition-all hover:-translate-y-2 active:scale-95 group text-sm sm:text-xl flex items-center justify-center gap-5"
                                            >
                                                Scale Your Brand
                                                <ArrowRight className="w-5 h-5 sm:w-7 sm:h-7 group-hover:translate-x-3 transition-transform" />
                                            </Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </article>

                    {/* Desktop Sidebar TOC */}
                    <aside className="sticky top-48 hidden lg:block space-y-12">
                        <div className="space-y-8">
                            <div className="flex items-center gap-4">
                                <div className="w-8 h-8 rounded-lg bg-[#004D40] flex items-center justify-center text-white">
                                    <List className="w-4 h-4" />
                                </div>
                                <h3 className="text-xs font-black uppercase tracking-[0.3em] opacity-40 leading-none">Outline</h3>
                            </div>
                            <nav className="space-y-8 border-l border-[#004D40]/5 pl-8">
                                {post.sections.map((section, idx) => (
                                    <a
                                        key={idx}
                                        href={`#section-${idx}`}
                                        className="block group relative"
                                    >
                                        <span className="absolute -left-[33px] top-1/2 -translate-y-1/2 w-2 h-2 rounded-full bg-[#FF6B35] opacity-0 group-hover:opacity-100 transition-opacity" />
                                        <span className="text-[10px] font-black text-[#FF6B35] block mb-2 opacity-60">Insight 0{idx + 1}</span>
                                        <span className="text-sm font-black text-[#004D40]/40 group-hover:text-[#004D40] transition-colors leading-tight block">
                                            {section.heading}
                                        </span>
                                    </a>
                                ))}
                            </nav>
                        </div>

                        {/* Sidebar CTA */}
                        <div className="p-8 bg-white border border-[#004D40]/5 rounded-[2.5rem] shadow-sm space-y-6">
                            <Sparkles className="w-8 h-8 text-[#FF6B35]" />
                            <h4 className="text-xl font-black leading-tight">Need a growth audit?</h4>
                            <p className="text-sm text-[#004D40]/60 font-medium">Let's look at your data and build a roadmap together.</p>
                            <Link href="/#contact" className="flex items-center justify-center w-full py-4 bg-[#004D40] text-white rounded-2xl font-black text-xs uppercase tracking-widest hover:bg-[#FF6B35] transition-colors">Start Review</Link>
                        </div>
                    </aside>
                </div>
            </main>

            {/* Recommendations Grid - Enhanced for all devices */}
            <section className="py-24 sm:py-64 bg-white relative">
                <div className="max-w-[1440px] mx-auto px-6 sm:px-12 md:px-24">
                    <div className="text-center space-y-6 sm:space-y-10 mb-20 sm:mb-40">
                        <span className="text-[10px] sm:text-xs font-black uppercase tracking-[0.5em] text-[#FF6B35]">More Excellence</span>
                        <h2 className="text-4xl sm:text-8xl font-black text-[#004D40] tracking-tighter">Further Insights</h2>
                        <div className="h-2 w-24 sm:w-40 bg-[#FF6B35] mx-auto rounded-full" />
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12 sm:gap-24">
                        {Object.values(blogPosts).filter(p => p.slug !== post.slug).slice(0, 3).map((p, i) => (
                            <Link key={i} href={`/blog/${p.slug}`} className="group flex flex-col space-y-8 sm:space-y-12">
                                <div className="relative h-80 sm:h-[500px] rounded-[3rem] sm:rounded-[5rem] overflow-hidden shadow-xl group-hover:shadow-[0_80px_160px_rgba(0,0,0,0.1)] transition-all duration-1000 group-hover:-translate-y-4">
                                    <Image src={p.image} alt={p.title} fill className="object-cover transition-transform group-hover:scale-110 duration-[2000ms]" />
                                    <div className="absolute inset-0 bg-black/10 group-hover:bg-transparent transition-colors" />
                                    <div className="absolute bottom-10 left-10">
                                        <span className="px-5 py-2.5 bg-white text-[#004D40] text-[10px] font-black uppercase tracking-widest rounded-full shadow-2xl">{p.category}</span>
                                    </div>
                                </div>
                                <div className="space-y-4 sm:space-y-6 px-4">
                                    <div className="flex items-center gap-4 text-[10px] sm:text-[12px] font-black uppercase tracking-[0.3em] opacity-30">
                                        <span>{p.date}</span>
                                        <span className="w-1.5 h-1.5 rounded-full bg-[#004D40]" />
                                        <span>{p.readTime}</span>
                                    </div>
                                    <h3 className="text-2xl sm:text-4xl font-black group-hover:text-[#FF6B35] transition-colors line-clamp-2 leading-[1.1] tracking-tighter">{p.title}</h3>
                                    <ChevronRight className="w-8 h-8 text-[#FF6B35] transition-transform group-hover:translate-x-4" />
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>
            </section>

            {/* Sticky Global Final CTA - High-Impact */}
            <section className="py-24 sm:py-64 bg-[#004D40] text-center px-4 sm:px-6 relative overflow-hidden">
                <div className="absolute inset-0 bg-[url('/grid.svg')] opacity-[0.05] bg-center [mask-image:radial-gradient(white,transparent_80%)]" />
                <motion.div
                    initial={{ opacity: 0, y: 50 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    className="max-w-6xl mx-auto space-y-12 sm:space-y-24 relative z-10"
                >
                    <h2 className="text-5xl sm:text-9xl font-black text-white leading-[0.9] tracking-tighter">Ready for <br /><span className="text-accent underline decoration-white/10 underline-offset-[10px] sm:underline-offset-[20px]">Digital Dominance</span>?</h2>
                    <p className="text-xl sm:text-4xl text-white/40 max-w-3xl mx-auto font-medium leading-relaxed">Join the selective group of industry leaders scaling with precision AI implementation. Your competitors are watching.</p>
                    <Link href="/#contact" className="inline-flex px-12 sm:px-24 py-6 sm:py-10 bg-accent text-white font-black rounded-[2.5rem] sm:rounded-[4rem] text-xl sm:text-3xl hover:bg-[#e85a2a] transition-all hover:shadow-[0_60px_120px_rgba(255,107,53,0.3)] hover:-translate-y-3 active:scale-95 group items-center gap-8">
                        Let&apos;s Get To Work
                        <ArrowRight className="w-8 h-8 sm:w-12 sm:h-12 group-hover:translate-x-6 transition-transform" />
                    </Link>
                </motion.div>
            </section>

            <Footer />
        </div>
    );
}

"use client";

import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Send,
    ArrowRight,
    MapPin,
    Globe,
    CheckCircle2,
    Sparkles,
    AlertCircle,
    Loader2
} from "lucide-react";

export default function Contact() {
    const [status, setStatus] = useState<"idle" | "loading" | "success" | "error">("idle");
    const [errorMessage, setErrorMessage] = useState("");

    // Form State
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: ""
    });

    // Handle Input Changes
    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value
        }));
    };

    // Handle Form Submission
    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setStatus("loading");
        setErrorMessage("");

        try {
            // Client-side validation (double check)
            if (!formData.name || !formData.email || !formData.message) {
                throw new Error("Please fill in all fields.");
            }

            const response = await fetch('/api/contact', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const result = await response.json();

            if (!response.ok) {
                throw new Error(result.error || "Failed to send message.");
            }

            // Success
            setStatus("success");
            setFormData({ name: "", email: "", message: "" });

            // Reset status after showing success message (optional, but keeps it clean)
            // User requested "Show confirmation message" - we keep it visible until they dismiss or navigate, 
            // but the prompt says "Clear form after successful submission". 
            // We'll keep the success state visible for a bit or let user click to send another.

        } catch (error: any) {
            console.error("Error submitting form:", error);
            setStatus("error");
            setErrorMessage(error.message || "Something went wrong. Please try again.");
        }
    };

    return (
        <section id="contact" className="relative py-24 overflow-hidden bg-primary">
            {/* Background Visuals */}
            <div className="absolute top-0 left-0 w-full h-full pointer-events-none overflow-hidden">
                <div className="absolute -top-24 -left-24 w-96 h-96 bg-accent opacity-10 rounded-full blur-[100px] animate-pulse" />
                <div className="absolute -bottom-24 -right-24 w-96 h-96 bg-white opacity-5 rounded-full blur-[120px]" />
                <div className="absolute inset-0 opacity-[0.03]"
                    style={{ backgroundImage: 'radial-gradient(circle at 2px 2px, white 1px, transparent 0)', backgroundSize: '40px 40px' }}
                />
            </div>

            <div className="container mx-auto px-6 lg:px-12 relative z-10">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">

                    {/* Left Side: Info & Text */}
                    <motion.div
                        initial={{ opacity: 0, x: -30 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="space-y-8"
                    >
                        <div className="space-y-6">
